import React, { Component, useState, useRef, useEffect } from 'react'
import { Map, GoogleApiWrapper, Marker, Polygon, InfoWindow } from 'google-maps-react';
import { usePosition } from 'use-position';
import GoogleMapReact from 'google-map-react';
import { MDBCard, MDBCardTitle, MDBCardText, MDBContainer } from "mdbreact";


import "./Style/UserPage.css"
import jwtDecode from 'jwt-decode'
import {MDBBtn} from "mdbreact"

const Perso = (props) => {
    const { color, name, id } = props;
    return (
        <div className="pin bounce"
             style={{ backgroundColor: color, cursor: 'pointer'}}
             title={name}
        />
    );
};

const Marker1 = (props) => {
    const {title, price, description, time} = props;
    return (
        <React.Fragment>
            <div
                style={{
                    border: "5px solid white",
                    borderRadius: 20,
                    height: 20,
                    width: 20
                }}
            />
            {/* Below is info window component */}
            {props.show && (
                <div>
                    <MDBContainer>
                        <MDBCard className="card-body" style={{width: "22rem", marginTop: "1rem"}}>
                            <MDBCardTitle>{title}</MDBCardTitle>
                            <MDBCardText>
                                {description}
                            </MDBCardText>
                            <div className="flex-row">
                                <p>{price}</p>
                                <p style={{marginLeft: "1.25rem"}}>{time}</p>
                            </div>
                        </MDBCard>
                    </MDBContainer>
                </div>
            )}
        </React.Fragment>
    )
}

const Annonce =  (props) => {
    const { color, name, id } = props;
    return (
        <Marker
             style={{ backgroundColor: color, cursor: 'pointer'}}
             title={name}
        />
    );
}

export class UserPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nom: '',
            prenom: '',
            latMe: '',
            lonMe: '',
            showingInfoWindow: false,  //Hides or the shows the infoWindow
            activeMarker: {},          //Shows the active marker upon click
            selectedPlace: {}          //Shows the infoWindow to the selected place upon a marker
        };
        this.logout = this.logout.bind(this);
        this.onMarkerClick = this.onMarkerClick.bind(this);
        this.onClose = this.onClose.bind(this);
    }


    logout(e) {
        e.preventDefault();
        localStorage.removeItem('token');
        this.props.history.push('/');
    }

    componentDidMount() {
        const tokken = localStorage.token;
        const decode = jwtDecode(tokken);

        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(location => {
                this.setState({
                    latMe: location.coords.latitude,
                    lonMe: location.coords.longitude
                })

            });
        }
        this.setState({
            prenom : decode.prenom,
            nom : decode.nom
        })
    }

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });

    onClose = props => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            });
        }
    };

    render() {
        return (
            <div className="main">
                <div id="viewport">
                    <div id="sidebar">
                        <header>
                            <p style={{color: "white"}}><strong>Bienvenue {this.state.nom}</strong></p>
                        </header>
                        <div>
                            <ul style={{color: "white"}} type="button" class="list-group"> Compte</ul>
                            <br/>
                            <ul style={{color: "white"}} type="button" className="list-group"> Contrat</ul>
                            <br/>
                            <ul style={{color: "white"}} type="button" className="list-group"> travaux effectué</ul>
                            <br/>
                            <ul style={{color: "white"}} type="button" className="list-group"> Paramètre</ul>
                            <MDBBtn style={{color: "white"}} onClick={this.logout}>Logout</MDBBtn>
                        </div>
                    </div>
                    <div>
                        <div className="map">
                            <Map
                                google={this.props.google}
                                zoom={14}

                                initialCenter={{
                                    lat: this.state.lonMe,
                                    lng: this.state.latMe
                                }}
                            >
                                <Marker
                                    title={'My position.'}
                                    name={'Me'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: this.state.latMe, lng: this.state.lonMe}} />
                                <Marker
                                    title={'The marker`s title will appear as a tooltip.'}
                                    name={'Commit boite de nuit'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.8322, lng: 2.3561}} />
                                <Marker
                                    name={'Server bar LeJacquie pour 2jour'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.822, lng: 2.3561}} />
                                <Marker
                                    name={'Commit Brasserie le Michel pour 8h'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.832, lng: 2.3811}} />
                                <Marker
                                    name={'Runner FootLocker urgent'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.812, lng: 2.3661}} />
                                <Marker
                                    name={'Cuisiner hotel ibis urgence'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.822, lng: 2.3691}} />
                                <Marker
                                    name={'Hotesse de nuit pour 3jour/semaine'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.852, lng: 2.3661}} />
                                <Marker
                                    name={'Vendeur de crêpe, job étudiant'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.841, lng: 2.3621}} />
                                <Marker
                                    name={'Voiturier Casino 2 semaines'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 43.4333, lng: 6.7333}} />
                                <Marker
                                    name={'Vendeur de glace 2jour/semaine'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 43.4313, lng: 6.7433}} />
                                <Marker
                                    name={'APE à epitech, temps partiel'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 43.3, lng: 5.4}} />
                                <Marker
                                    name={'Testeur de bière, poste à pourvoir urgent'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 48.4, lng: -4.4833}} />
                                <Marker
                                    name={'Pizzabereiter'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 52.520007, lng: 13.404954}} />
                                <Marker
                                    name={'알코올 테스터'}
                                    onClick={this.onMarkerClick}
                                    position={{lat: 37.566535, lng: 126.9779692}} />
                                <InfoWindow
                                    marker={this.state.activeMarker}
                                    visible={this.state.showingInfoWindow}
                                    onClose={this.onClose} >
                                    <div>
                                        <h4>{this.state.selectedPlace.name}</h4>
                                    </div>
                                </InfoWindow>
                            </Map>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}


export default GoogleApiWrapper({
    apiKey: 'AIzaSyDW41KMRzwFp4m7Uht_53PiPHv0LSqXq5Y'
})(UserPage);
