import React, { Component, useState, useRef, useEffect } from 'react'
import useWindowScrollPosition from '@rehooks/window-scroll-position'
import Navbar from 'react-bootstrap/Navbar'
import 'bootstrap/dist/css/bootstrap.min.css';
import MobileStoreButton from 'react-mobile-store-button';
import Login from './Login'
import Register from './Register'
import UserPage from './UserPage'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    NavLink
} from "react-router-dom";
import { MDBBtn } from "mdbreact";
import './Style.css'
import Recruteur from './Recruteur';

function Navi() {
    const [change, setChange] = useState(false);
    const changePosition = 60;

    let position = useWindowScrollPosition();
    // position == { x: 0, y: 0
    if (position.y > changePosition && !change) {
        setChange(true);
    }
    if (position.y <= changePosition && change) {
        setChange(false);
    }
    let style = {
        backgroundColor: change ? "#5A09F0" : "transparent",
        transition: "400ms ease",
        height: "100px",
        position: "fixed",
        right: 0,
        left: 0,
        top: 0
    };

    return (
        <div style={style}>
            <Navbar className="navbar fixed-top navbar-expand-lg navbar-dark scrolling-navbar ">
                <div className="container">
                    <a className="navbar-brand" href="/">
                        <strong>JoBizz</strong>
                    </a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse"
                            data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent"
                            aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item active">
                                <a className="nav-link" href="#">Home
                                    <span className="sr-only">(current)</span>
                                </a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#"
                                   target="_blank">About JoBizz</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </Navbar>
        </div>
    );
}

function Presentation() {
    return (
        <div className="jumbotron">
            <div className="text-white text-center py-5 px-4">
                <div>
                    <div className="left">
                        <div className="hr-light">
                            <div className="TextDescription">
                                <h1 className="display-2 font-weight-bold">JoBizz</h1>
                                <h2>
                                    <strong>Job Saisonnier et Extra.</strong>
                                </h2>
                                <p><strong>Tous les jobs disponible autour de chez toi en un clique.</strong></p>
                            </div>
                        </div>
                        <div className="AppStore">
                            <MobileStoreButton
                                store="ios"
                                url=""
                                linkProps={{ title: 'iOS Store Button' }}
                            />
                            <MobileStoreButton
                                store="android"
                                url=""
                                linkProps={{ title: 'android Store Button' }}
                            />
                        </div>
                        <div className="Connect">
                            <Link to='/Login'><MDBBtn type="button" class="btn btn-primary">Accéder a la plateforme</MDBBtn></Link>
                        </div>
                    </div>
                    <div className="right">
                        <figure className="mobile-image wow fadeInUp" data-wow-delay="0.2s">
                            <img src={require('../image/finale1.png')} />
                        </figure>
                    </div>
                </div>
            </div>
        </div>
    )
}

class LandingPage extends Component {
    render() {
        return (
            <div className="landingPage">
                <Navi/>
                <Router>
                    <Route exact path="/" component={Presentation}/>
                    <Route path="/Login" component={Login}/>
                    <Route path="/Register" component={Register}/>
                    <Route path="/UserPage" component={UserPage}/>
                    <Route path="/Recruteur" component={Recruteur}/>
                </Router>
            </div>
        );
    }
}

export default LandingPage;
